INSERT INTO `#__jd_email_templates` (`id`, `email_key`, `subject`, `content`) VALUES
(1, 'admin_email_body', 'New Donation', '<table style=\"max-width: 600px; margin: auto; background: #ffffff; border-radius: 8px; box-shadow: 0 2px 6px rgba(0,0,0,0.03);\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 24px 32px;\">\r\n<h2 style=\"color: #388e3c; margin-top: 0;\">You Have Received a New Donation!</h2>\r\n<p style=\"color: #222;\">Dear Administrator,</p>\r\n<p style=\"color: #222;\">A new donation has been made to your campaign. Here are the details:</p>\r\n<table style=\"background: #f8f9fb; border-radius: 6px;\" width=\"100%\" cellspacing=\"0\" cellpadding=\"6\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Campaign:</td>\r\n<td>[CAMPAIGN]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Donor Name:</td>\r\n<td>[FIRST_NAME] [LAST_NAME]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Organization:</td>\r\n<td>[ORGANIZATION]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Address:</td>\r\n<td>[ADDRESS] [ADDRESS2]<br />[CITY], [STATE] [ZIP]<br />[COUNTRY]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Phone:</td>\r\n<td>[PHONE]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Fax:</td>\r\n<td>[FAX]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Email:</td>\r\n<td>[EMAIL]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Amount:</td>\r\n<td style=\"color: #388e3c; font-weight: bold;\">[AMOUNT]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Comment:</td>\r\n<td>[COMMENT]</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"color: #666; font-size: 13px; margin-top: 24px;\">This is an automated notification from your website. Please do not reply to this email.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
(2, 'user_email_body', 'Donation Confirmation', '<table style=\"max-width: 600px; margin: auto; background: #ffffff; border-radius: 8px; box-shadow: 0 2px 6px rgba(0,0,0,0.03);\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 24px 32px;\">\r\n<h2 style=\"color: #388e3c; margin-top: 0;\">Thank You for Your Donation!</h2>\r\n<p style=\"color: #222;\">Dear [FIRST_NAME] [LAST_NAME],</p>\r\n<p style=\"color: #222;\">Thank you for your generous donation to our site. We appreciate your support.<br />Here are the details of your donation:</p>\r\n<table style=\"background: #f8f9fb; border-radius: 6px;\" width=\"100%\" cellspacing=\"0\" cellpadding=\"6\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Campaign:</td>\r\n<td>[CAMPAIGN]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Organization:</td>\r\n<td>[ORGANIZATION]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Address:</td>\r\n<td>[ADDRESS] [ADDRESS2]<br />[CITY], [STATE] [ZIP]<br />[COUNTRY]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Phone:</td>\r\n<td>[PHONE]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Email:</td>\r\n<td>[EMAIL]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Amount:</td>\r\n<td style=\"color: #388e3c; font-weight: bold;\">[AMOUNT]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Comment:</td>\r\n<td>[COMMENT]</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"color: #222; margin-top: 24px;\">If you have any questions, please contact us at [SITE_EMAIL].</p>\r\n<p style=\"color: #666; font-size: 13px; margin-top: 24px;\">Best regards,<br />Website Administrator Team</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
(3, 'user_email_body_offline', NULL, '<table style=\"max-width: 600px; margin: auto; background: #ffffff; border-radius: 8px; box-shadow: 0 2px 6px rgba(0,0,0,0.03);\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 24px 32px;\">\r\n<h2 style=\"color: #388e3c; margin-top: 0;\">Thank You for Your Donation!</h2>\r\n<p style=\"color: #222;\">Dear [FIRST_NAME] [LAST_NAME],</p>\r\n<p style=\"color: #222;\">Thank you for choosing to support our site with your generous donation.<br />Here are the details of your donation:</p>\r\n<table style=\"background: #f8f9fb; border-radius: 6px;\" width=\"100%\" cellspacing=\"0\" cellpadding=\"6\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Campaign:</td>\r\n<td>[CAMPAIGN]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Organization:</td>\r\n<td>[ORGANIZATION]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Address:</td>\r\n<td>[ADDRESS] [ADDRESS2]<br />[CITY], [STATE] [ZIP]<br />[COUNTRY]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Phone:</td>\r\n<td>[PHONE]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Email:</td>\r\n<td>[EMAIL]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Amount:</td>\r\n<td style=\"color: #388e3c; font-weight: bold;\">[AMOUNT]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Comment:</td>\r\n<td>[COMMENT]</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 style=\"color: #1a237e; margin-top: 32px;\">Offline Payment Instructions</h3>\r\n<p style=\"color: #222;\">Please send your donation as soon as possible to our bank account below:</p>\r\n<table style=\"background: #e3f2fd; border-radius: 6px;\" width=\"100%\" cellspacing=\"0\" cellpadding=\"6\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Account Name:</td>\r\n<td>Tuan Pham Ngoc</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Bank:</td>\r\n<td>Ngan Hang Ngoai Thuong Vietcombank</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Account Number:</td>\r\n<td>XXX045485467</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"color: #222; margin-top: 18px;\">We look forward to receiving your payment. If you have any questions, please contact us at [SITE_EMAIL].</p>\r\n<p style=\"color: #666; font-size: 13px; margin-top: 24px;\">Best regards,<br />Website Administrator Team</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
(4, 'user_email_body_offline_received', NULL, '<table style=\"max-width: 600px; margin: auto; background: #ffffff; border-radius: 8px; box-shadow: 0 2px 6px rgba(0,0,0,0.03);\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 24px 32px;\">\r\n<h2 style=\"color: #388e3c; margin-top: 0;\">Your Donation Has Been Received!</h2>\r\n<p style=\"color: #222;\">Dear [FIRST_NAME] [LAST_NAME],</p>\r\n<p style=\"color: #222;\">We are pleased to inform you that we have received your donation via offline payment.<br />Thank you very much for your generous support!</p>\r\n<table style=\"background: #f8f9fb; border-radius: 6px;\" width=\"100%\" cellspacing=\"0\" cellpadding=\"6\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Campaign:</td>\r\n<td>[CAMPAIGN]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Amount:</td>\r\n<td style=\"color: #388e3c; font-weight: bold;\">[AMOUNT]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Date Received:</td>\r\n<td>[RECEIVED_DATE]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Comment:</td>\r\n<td>[COMMENT]</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"color: #222; margin-top: 24px;\">Your contribution makes a real difference. If you have any questions or need further assistance, please contact us at [SITE_EMAIL].</p>\r\n<p style=\"color: #666; font-size: 13px; margin-top: 24px;\">Once again, thank you for your support!<br />Best regards,<br />Website Administrator Team</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
(5, 'recurring_email_body', 'New Payment From Recurring Donation', '<table style=\"max-width: 600px; margin: auto; background: #ffffff; border-radius: 8px; box-shadow: 0 2px 6px rgba(0,0,0,0.03);\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 24px 32px;\">\r\n<h2 style=\"color: #388e3c; margin-top: 0;\">New Recurring Donation Processed</h2>\r\n<p style=\"color: #222;\">Hello Admin,</p>\r\n<p style=\"color: #222;\">A new payment has been received from a recurring donation.</p>\r\n<table style=\"background: #f8f9fb; border-radius: 6px;\" width=\"100%\" cellspacing=\"0\" cellpadding=\"6\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Donation Amount:</td>\r\n<td>[DONATION_AMOUNT]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Donor Name:</td>\r\n<td>[DONOR_NAME]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Date:</td>\r\n<td>[DATE]</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"color: #222; margin-top: 24px;\">You can <a style=\"color: #1976d2; text-decoration: underline;\" href=\"[DONATION_LINK]\">click here</a> to view the details of this donation record.</p>\r\n<p style=\"color: #666; font-size: 13px; margin-top: 24px;\">This is an automated notification from your website donation system.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
(6, 'new_campaign_email_body', 'New Campaign Submitted: [CAMPAIGN_TITLE]', '<table style=\"max-width: 600px; margin: auto; background: #ffffff; border-radius: 8px; box-shadow: 0 2px 6px rgba(0,0,0,0.03);\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 24px 32px;\">\r\n<h2 style=\"color: #1976d2; margin-top: 0;\">New Campaign Created</h2>\r\n<p style=\"color: #222;\">Hello Admin,</p>\r\n<p style=\"color: #222;\">A new campaign has been submitted on your website.</p>\r\n<table style=\"background: #f8f9fb; border-radius: 6px;\" width=\"100%\" cellspacing=\"0\" cellpadding=\"6\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Campaign Title:</td>\r\n<td>[CAMPAIGN_TITLE]</td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-weight: bold;\">Submitted By:</td>\r\n<td>[OWNER]</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"color: #222; margin-top: 24px;\">You can <a style=\"color: #1976d2; text-decoration: underline;\" href=\"[CAMPAIGN_LINK]\">click here</a> to review the campaign details.</p>\r\n<p style=\"color: #666; font-size: 13px; margin-top: 24px;\">This is an automated notification from your website.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
(7, 'campaign_owner_notification_body', 'Congratulations! Your campaign [CAMPAIGN_TITLE] has received a new donation', '<table style=\"max-width: 600px; margin: auto; background: #ffffff; border-radius: 8px; box-shadow: 0 2px 6px rgba(0,0,0,0.03);\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 24px 32px;\">\r\n<h2 style=\"color: #388e3c; margin-top: 0;\">Great News!</h2>\r\n<p style=\"color: #222;\">Dear [OWNER],</p>\r\n<p style=\"color: #222;\">Your campaign <strong>[CAMPAIGN_TITLE]</strong> has just received a new donation!</p>\r\n<p style=\"color: #222;\">You can <a style=\"color: #1976d2; text-decoration: underline;\" href=\"[CAMPAIGN_LINK]\">click here</a> to view your campaign and see the details.</p>\r\n<p style=\"color: #222;\">Thank you for your efforts and dedication. We wish you continued success with your campaign!</p>\r\n<p style=\"color: #666; font-size: 13px; margin-top: 24px;\">This is an automated notification from your website.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
(8, 'share_campaign_body', 'Help Us Make a Difference ', '<p>Dear [FRIEND_NAME],<br />We hope this message finds you well. Today, we\'re reaching out to share an exciting opportunity to make a real difference in the lives of those who need it most.<br />At [CAMPAIGN_NAME], we are dedicated to [briefly describe your cause</p>');